import Foundation

public typealias StringClosure = () -> String
public typealias OptionalStringClosure = () -> String?

public typealias BoolClosure = () -> Bool
public typealias OptionalBoolClosure = () -> Bool?

public typealias IntClosure = () -> Int
public typealias OptionalIntClosure = () -> Int?

public typealias DateClosure = () -> Date
public typealias OptionalDateClosure = () -> Date?

